package com.java;

public class LogicalOpe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//variable definition and Initialization
		
		boolean bool1 = true , bool2 = false;
		
		//logical AND
		System.out.println("bool1 && bool2 =" +(bool1 && bool2));
		
		//logical OR
		System.out.println("bool1 || bool2 =" +(bool1 || bool2));
		
		//logical NOT
		System.out.println("!(bool1 && bool2 =)" +!(bool1 && bool2));
	}

}
